    return(
        <View style={{ flex: 1, backgroundColor:'#E0FFFF'}}>
            
          <TouchableOpacity >  <Image style={styles.imagestyle} source={require('./img/icone.png')} /></TouchableOpacity>

          <ImageBackground source={require ('./img/ChampRbp.png')} resizeMode="contain" style={styles.ChampRbp}></ImageBackground>

          <ImageBackground source={require ('./img/ChampFr.png')} resizeMode="contain" style={styles.ChampFr}>

          </ImageBackground>

        </View>
   )
 }

const styles = StyleSheet.create({
